
public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Programik z pêtlami");
		for (int i = 0; i < 10 ; i ++)
		{
			System.out.print(i + " ");
		}
		System.out.println("");
		for (int i = 9; i >= 0 ; i --)
		{
			System.out.print(i + " ");
		}
		System.out.println("");
		for (int i = 0; i < i*2 ; i ++)
		{
			System.out.print(i + " ");
		}
	}

}
